import pytest
import copy

from sudoku.solver import (
    str_to_board, 
    load_all_sudoku_boards,
    SudokuSolver,
    are_peers,
)


# LOADING INPUT
def test_smaller_input_size():
    fn = "tests/data/smaller_input.txt"
    with pytest.raises(AssertionError):
        boards = load_all_sudoku_boards(fn)

def test_larger_input_size():
    fn = "tests/data/larger_input.txt"
    with pytest.raises(AssertionError):
        boards = load_all_sudoku_boards(fn)

def test_larger_input_size2():
    fn = "tests/data/larger_input2.txt"
    with pytest.raises(AssertionError):
        boards = load_all_sudoku_boards(fn)

def test_char_input():
    fn = "tests/data/haschar.txt"
    with pytest.raises(ValueError):
        boards = load_all_sudoku_boards(fn)

def test_delimiter_input():
    fn = "tests/data/delimiter.txt"
    with pytest.raises(AssertionError):
        boards = load_all_sudoku_boards(fn)

# EXPECTED PATH

# load once
@pytest.fixture(scope='session')
def single_board():
    fn = "tests/data/1sudoku.txt"
    boards = load_all_sudoku_boards(fn)
    assert(len(boards)==1)
    return copy.deepcopy(boards[0])

def test_1sudoku(single_board):
    expected_solution = [
        [2, 9, 4, 8, 6, 3, 5, 1, 7], 
        [7, 1, 5, 4, 2, 9, 6, 3, 8], 
        [8, 6, 3, 7, 5, 1, 4, 9, 2], 
        [1, 5, 2, 9, 4, 7, 8, 6, 3], 
        [4, 7, 9, 3, 8, 6, 2, 5, 1], 
        [6, 3, 8, 5, 1, 2, 9, 7, 4], 
        [9, 8, 6, 1, 3, 4, 7, 2, 5], 
        [5, 2, 1, 6, 7, 8, 3, 4, 9], 
        [3, 4, 7, 2, 9, 5, 1, 8, 6]
    ]
    solver = SudokuSolver(single_board)
    solver.solve()
    assert(single_board==expected_solution)

def test_are_peers():
    p1 = (2, 2)
    p2 = (2, 3)
    p3 = (3, 3)
    assert are_peers(p1, p2)
    assert are_peers(p2, p3)
    with pytest.raises(AssertionError):
        assert are_peers(p1, p3) # different boxes

